
Add-Type -AssemblyName System.Windows.Forms


$form = New-Object System.Windows.Forms.Form
$form.Size = New-Object System.Drawing.Size(850, 600)
$form.MinimumSize = $form.Size


$icon = "resources\icon.ico"
$form.Icon = [System.Drawing.Icon]::ExtractAssociatedIcon($icon)


$form.Text = "Universal Uninstaller"
$form.StartPosition = [System.Windows.Forms.FormStartPosition]::CenterScreen

 <#This base64 string holds the bytes that make up the orange 'G' icon (just an example for a 32x32 pixel image)
 #https://products.aspose.app/svg/encoding/png-to-base64
$iconBase64      = ''
$iconBytes       = [Convert]::FromBase64String($iconBase64)
# initialize a Memory stream holding the bytes
$stream          = [System.IO.MemoryStream]::new($iconBytes, 0, $iconBytes.Length)
$Form.Icon       = [System.Drawing.Icon]::FromHandle(([System.Drawing.Bitmap]::new($stream).GetHIcon())) #>

#https://www.flaticon.com/de/kostenlose-icons/fernzugriff
#https://icon-icons.com/de/symbol/Runde-remote-desktop/132781

$form.ShowDialog()

